//------------------------------------------------------------------------------------------------------
// 编辑器预览窗口运行时提供的系统变量
// 关于预览的流程：
// -- 1.imageLayerPassageClear 每已使用的通道x每已使用的指令
// -- 2.runCommand 每个指令按顺序执行一遍
// -- 3.imageLayerPassageStart 每已使用的通道x每已使用的指令
// 关于播放的流程
// -- 1.imageLayerPassageClear 每已使用的通道x每已使用的指令
// -- 2.runCommand 每个指令按顺序播放
//------------------------------------------------------------------------------------------------------
/**
 * 当前自定义指令的编号
 */
declare var commandID: number;
/**
 * 图像层通道数据
 */
declare var imageLayerPassageData: any[];
/**
 * 图像层显示对象容器
 */
declare var imageLayer: GameImageLayer;
/**
 * 界面层显示对象容器
 */
declare var uiLayer: GameSprite;
/**
 * 预览窗口
 */
declare var previewWin: {
    /**
     * 清理全部事件指令产生的预览效果
     */
    clearData: Function;
    /**
     * 设置数据，刷新预览效果直到当前选中的指令行
     */
    setData: Function;
    /**
     * 当前选中的指令
     */
    currentSelectCmd: Command;
    /**
     * 视图区域是否右键移动过
     */
    viewAreaRightMouseMove: boolean;
    /**
     * 界面层显示优先级计数 从0开始
     */
    uiLayerDisplayPriority: number;
    /**
     * 允许显示全部对象
     */
    allowAllDisplayObject: boolean;
    /**
     * 对象操作工具
     */
    operateObjectTool: {
        // 刷新对象操作工具，比如来自其他地方更改了对象的位置或尺寸等，可使用该函数刷新
        refresh: Function,
        // 选中显示对象
        selectObjects: (sp: GameSprite[]) => void,
        // 需要排除缩放和旋转工具的对象
        addExcludeOperactionObject: (sp: GameSprite) => void
    }
    /**
     * 选中事件指令
     * @param cmd 事件指令
     * @param sendEvent 派发事件 默认=false 派发事件会刷新属性栏、预览窗口等
     */
    selectCmdMessageByCmd(cmd: Command, sendEvent?: boolean);
    /**
     * 修改指令属性
     * @param cmd 需要修改的指令
     * @param varName 参数变量名称
     * @param newValue 新的值
     */
    modifyCommandParam: (cmd: Command, varName: string, newValue: any) => void;
    /**
     * 新增指令 默认是默认值，如果有写入参数的话则以参数为准 varNameValue
     * @param customID 自定义ID
     * @param varNameValue 额外的变量值，如存在则优先使用该值，否则使用自定义编辑器中的默认值，如 {dpX:100,dpY:200}
     * @param selectedIndex [可选] 默认值=-2  -3=当前选中（之后） -2=当前选中（之前） -1=最尾端 0-N=自定义 
     * @param refreshPreViewArea [可选] 默认值=true 刷新预览区
     */
    insertCustomCMD(customID: number, varNameValue: any, selectedIndex?: number, refreshPreViewArea?: boolean): Command;
    /**
     * 新增指令-系统
     * @param cmdID 
     * @param params 
     * @return [Command] 
     */
    insertSystemCMD(cmdID: number, params: any[], selectedIndex?: number): Command;
    /**
     * 更改显示层
     * @param selectedIndex 0-界面层 1-图像层
     */
    changeLayer(selectedIndex: number): void;
    /**
     * 创建菜单
     * var menuData:any[] = [
               {
                   label:"重命名",
                   splitLine:false,// 是否显示分割线
                   children:[], // 存放和本身这个对象是一样的格式
                   execute:"functionName", // 点击后执行的方法名称
                   shortcutKey:"", //显示的快捷键名称
                   menuHotKey:"", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                   enabled:true, // 是否可用，不填写表示true
                   visible:true, // 是否显示，不填写表示true
                   selected:true, // 如果存在则以打钩的形式选择此项
                   group:"myGroupName" // 配合selected，如若存在则同组单选
               }
           ];
     * @param menuData 菜单数据
     * @param x 全局坐标X
     * @param y 全局坐标Y
     * @param executeDomain 函数作用域
     */
    createMenu(menuData: any[], x: number, y: number, executeDomain: any);
}
//------------------------------------------------------------------------------------------------------
// 通道数据
//------------------------------------------------------------------------------------------------------
declare class UIPassageData {
    // ====> system 必要项
    /**
     * 通道编号
     */
    passageID: number;
    /**
     * 类别，一般用于区分显示对象，如图片、动画、立绘、界面都是属于不同的类别
     */
    type: string;
    /**
     * 记录关联的指令
     */
    cmd: Command;
    /**
     * 当通道覆盖时调用的函数，以便清理掉。如显示了2号图片或移动了2号图片，再次显示2号图片时会清理掉此前显示的图片或移动行为
     */
    onCoverPassage: Function;

    // ====> custom 自定义属性
    dpDisplayPriority: number; // 显示层次优先级
    passageIDUseVar: boolean;
    uiID: number;
    displayObject: GUI_BASE;
    setAttr: boolean;
    displayObjectFromShow: boolean; // 区分显示对象来源，是否来自显示图片，否则来自移动图片
    dpX: number;
    dpY: number;
    dpZ: number;
    dpScaleX: number;
    dpScaleY: number;
    rotation: number;
    opacity: number;

    showType: number;
    objectUseVar: boolean;
    refObjectEnabled: boolean;
    refObject: number;
    lastStandAvatarCmdParams: ThisCommandParams; // 记录上一个关联的指令的参数
}
//------------------------------------------------------------------------------------------------------
// 自定义变量
//------------------------------------------------------------------------------------------------------
var passageType = "gcUI";
var PLUGIN_CUSTOM_COMMAND_showUI: number = 3010;
var PLUGIN_CUSTOM_COMMAND_moveUI: number = 3011;
var PLUGIN_CUSTOM_COMMAND_closeUI: number = 3012;
var newCmdParams;
var newCmdData: UIPassageData;

//------------------------------------------------------------------------------------------------------
// [everyone used command] 逐指令行目执行
//------------------------------------------------------------------------------------------------------
/**
 * 逐指令执行：对应事件下按照从上至下的顺序逐行执行，如果是该自定义指令时则会执行该函数
 * 该函数用于编辑器预览和播放，如果分歧面板中筛选不经过的事件指令则不会执行该函数。
 * 比如事件页中有3个该指令，则按照顺序执行3次该函数
 * @param cmdParams 指令的自定义参数数据，根据用户设定值
 * @param cmd 对应的指令对象
 * @param isPlayMode 是否是播放模式，否则是普通的预览模式
 */
function runCommand(cmdParams: ThisCommandParams, cmd: Command, isPlayMode: boolean) {
    // -- 图像层的场合
    if (cmdParams.showType == 1) {
        var passageID = cmdParams.passageID;
        // 使用变量作为通道的话则忽略此指令执行
        if (cmdParams.passageIDUseVar) {
            return;
        }
    }
    // -- 界面层的场合
    else {
        passageID = 1000000 + cmdParams.uiID;
    }
    // -- 获取通道数据
    var passageData: UIPassageData = imageLayerPassageData[passageID];
    // -- 播放模式下
    if (isPlayMode) {
        // 覆盖通道，调用上一个占用该通道的指令的覆盖函数（不一定是该指令）
        if (passageData) {
            passageData.onCoverPassage && passageData.onCoverPassage.apply(this, [passageID, passageData]);
        }
    }
    // -- 获取储存的通道，没有的话则新建
    passageData = imageLayerPassageData[passageID] = {} as any;
    // 写入数据：系统必要项
    passageData.passageID = passageID;
    passageData.type = passageType;
    passageData.cmd = cmd;
    passageData.lastStandAvatarCmdParams = cmdParams;
    passageData.objectUseVar = cmdParams.objectUseVar;
    passageData.refObjectEnabled = cmdParams.refObjectEnabled;
    passageData.refObject = cmdParams.refObject;
    // 写入数据：自定义属性（预览不支持变量效果显示）
    if (cmdParams.objectUseVar) {
        if (cmdParams.refObjectEnabled) {
            passageData.uiID = cmdParams.refObject ? cmdParams.refObject : 0;
        }
        else {
            passageData.uiID = 0;
        }
    }
    else {
        passageData.uiID = cmdParams.uiID ? cmdParams.uiID : 0;
    }
    if (cmdParams.setAttr) {
        passageData.dpX = cmdParams.dpX;
        passageData.dpY = cmdParams.dpY;
        passageData.dpZ = cmdParams.dpZ;
        passageData.dpScaleX = cmdParams.dpScaleX;
        passageData.dpScaleY = cmdParams.dpScaleY;
        passageData.rotation = cmdParams.rotation;
        passageData.opacity = cmdParams.opacity;
    }
    passageData.setAttr = cmdParams.setAttr;
    passageData.displayObjectFromShow = true;
    passageData.showType = cmdParams.showType;
    // 显示优先级
    if (cmdParams.showType == 0) {
        passageData.dpDisplayPriority = previewWin.uiLayerDisplayPriority++;
    }

    // 播放模式的情况：需要创建对象以便播放
    if (isPlayMode) {
        // 记录覆盖函数，以便其他指令可以覆盖该通道时
        passageData.onCoverPassage = imageLayerPassageClear;
        // 创建图片显示对象
        var a = GameUI.load(passageData.uiID, true);
        a.data = passageData;
        // a.syncLoadWhenAssetExist = true;
        // 设定显示优先度，以便在同Z轴时，编号越大，显示在越前方
        a.useDPCoordScaleMode = true;
        if (passageData.showType == 1) {
            imageLayer.addChild(a);
            a.dpDisplayPriority = passageData.passageID;
            a.useDPCoord = true;
            if (cmdParams.setAttr) {
                a.dpX = passageData.dpX;
                a.dpY = passageData.dpY;
                a.dpZ = passageData.dpZ;
                a.dpScaleX = passageData.dpScaleX;
                a.dpScaleY = passageData.dpScaleY;
                a.rotation1 = passageData.rotation;
                a.opacity = passageData.opacity;
            }
            // 刷新坐标
            a.dpCoordToRealCoord();
        }
        else {
            uiLayer.addChild(a);
            a.dpDisplayPriority = passageData.dpDisplayPriority;
            if (cmdParams.setAttr) {
                a.x = passageData.dpX;
                a.y = passageData.dpY;
                a.scaleX = passageData.dpScaleX;
                a.scaleY = passageData.dpScaleY;
                a.width = Config.WINDOW_WIDTH;
                a.height = Config.WINDOW_HEIGHT;
                a.rotation1 = passageData.rotation;
                a.opacity = passageData.opacity;
            }
        }
        // 记录显示对象
        passageData.displayObject = a;
    }
}
/**
 * 编辑器内预览播放时预加载
 */
function inEditorPlayPreload(onFin: Function, cmdParams: ThisCommandParams) {
    var toUI: number;
    // 使用变量的情况下
    if (cmdParams.objectUseVar) {
        if (cmdParams.refObjectEnabled) {
            toUI = cmdParams.refObject ? cmdParams.refObject : 0;
        }
        else {
            toUI = 0;
        }
    }
    // 普通显示
    else {
        toUI = cmdParams.uiID ? cmdParams.uiID : 0;
    }
    AssetManager.preLoadUIAsset(toUI, Callback.New(onFin, this), true);
}
//------------------------------------------------------------------------------------------------------
// 属性面板修改：当修改该指令的属性时，只会执行该指令对应的「指令源」的这些函数
// 如A指令来自自定义指令32号，每次通过属性面板快速修改A指令的参数b时，则会执行32号指令的下面2个函数
// [current used command] 
//------------------------------------------------------------------------------------------------------
/**
 * 当属性面板中的属性修改前
 * @param cmd 对应的指令
 * @param cmdParams 对应指令的参数 
 * @param varName 修改的属性名
 */
function onCommandAttributeBeforeChange(cmd: Command, cmdParams: ThisCommandParams, varName: string) {
    // 修改了通道的话，清理
    if (varName == "passageID" || varName == "setAttr" || varName == "passageIDUseVar" || varName == "showType" || varName == "uiID" || varName == "refObject" || varName == "refObjectEnabled" || varName == "objectUseVar") {
        previewWin.clearData();
    }
}
/**
 * 当属性面板中的属性修改后
 * @param cmd 对应的指令
 * @param cmdParams 对应指令的参数 
 * @param varName 修改的属性名
 * @param newValue 修改后的值
 */
function onCommandAttributeChange(cmd: Command, cmdParams: ThisCommandParams, varName: string, newValue: any) {
    // 修改了通道的话，重置所有预览显示
    if (varName == "passageID" || varName == "setAttr" || varName == "passageIDUseVar" || varName == "uiID" || varName == "refObject" || varName == "refObjectEnabled" || varName == "objectUseVar" || varName == "showType") {
        previewWin.setData(false);
        return;
    }
    // -- 图像层的场合
    if (cmdParams.showType == 1) {
        var passageID = cmdParams.passageID;
        // 使用变量作为通道的话则忽略此指令执行
        if (cmdParams.passageIDUseVar) {
            return;
        }
    }
    // -- 界面层的场合
    else {
        passageID = 1000000 + cmdParams.uiID;
    }
    // 获取通道数据
    var passageData: UIPassageData = imageLayerPassageData[passageID];
    if (!passageData) return;
    // 对象类别匹配且存在显示对象的话
    if (passageData.type == passageType && passageData.displayObject) {
        // 混合模式，需要转换
        if (varName == "rotation") {
            passageData.displayObject.rotation1 = newValue;
        }
        else {
            // 界面层的情况
            if (cmdParams.showType == 0) {
                var attrNameMapping = { dpX: "x", dpY: "y", dpScaleX: "scaleX", dpScaleY: "scaleY" };
                if (attrNameMapping[varName]) passageData.displayObject[attrNameMapping[varName]] = newValue;
                else passageData.displayObject[varName] = newValue;
            }
            else {
                passageData.displayObject[varName] = newValue;
            }
        }
        // -- 刷新操作工具对象（延迟到下一帧）
        Callback.CallLaterBeforeRender(previewWin.operateObjectTool.refresh, previewWin.operateObjectTool);
    }
}
//------------------------------------------------------------------------------------------------------
// 对象操作工具
// [everyone used command type] 遍历每个执行过的自定义指令类别
//------------------------------------------------------------------------------------------------------
/**
 * 当操作工具移动对象时
 * @param realSelObjs 选中的对象
 */
function imageLayerOnToolsChangePostion(realSelObjs: GameSprite[]): void {
    imageLayerOnToolsChange(0, realSelObjs);
}
/**
 * 当操作工具缩放时
 * @param realSelObjs 选中的对象
 */
function imageLayerOnToolsChangeSize(realSelObjs: GameSprite[]) {
    imageLayerOnToolsChange(1, realSelObjs);
}
/**
 * 当操作工具旋转时
 * @param realSelObjs 选中的对象
 */
function imageLayerOnToolsChangeRotation(realSelObjs: GameSprite[]) {
    imageLayerOnToolsChange(2, realSelObjs);
}
/**
 * 当操作工具开始操作时
 */
function imageLayerOnToolsChangeStart(realSelObjs: GameSprite[]) {

}
/**
 * 当操作工具结束操作时
 */
function imageLayerOnToolsChangeEnd(realSelObjs: GameSprite[]) {
    if (newCmdParams == null) return;
    if (realSelObjs.length == 0) return;
    var data: any = realSelObjs[0].data;
    if (!data || data.type != passageType) return;
    var newCMD = previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_moveUI, newCmdParams, -2, false);
    newCmdData.cmd = newCMD;
    newCmdParams = null;
    previewWin.setData();
}
/**
 * 当操作工具选中对象时
 */
function imageLayerOnToolsSelectObject(realSelObjs: GameSprite[]) {

}
//------------------------------------------------------------------------------------------------------
// 图像层相关 遍历每个已使用的通道，每个已使用的通道再遍历已使用的指令，调用下面的函数
// [everyone passage] x [everyone used command type]
//------------------------------------------------------------------------------------------------------
/**
 * 清理函数：每次刷新前调用此函数进行清理
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageClear(passageID: number, passageData: UIPassageData) {
    // -- 判断该通道是该类别
    if (passageData.type == passageType) {
        // 卸载创建的显示对象
        var a: GUI_BASE = passageData.displayObject;
        if (a) {
            a.dispose();
        }
    }
}
/**
 * 开始函数
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageStart(passageID: number, passageData: UIPassageData) {
    // 不是来自显示图片的话忽略掉（比如只有移动图片指令实际生效）
    if (!passageData.displayObjectFromShow) {
        return;
    }
    // 通道类别匹配的话
    if (passageData.type == passageType) {
        passageData.onCoverPassage = imageLayerPassageClear;// v0.985 upgrader

        // -- 创建图片
        var a = GameUI.load(passageData.uiID, true);
        // a.syncLoadWhenAssetExist = true;
        a.data = passageData;
        passageData.displayObject = a;
        if (passageData.showType == 1) {
            imageLayer.addChild(a);
            // -- 同Z轴时按该优先级出现，即编号越大yue
            a.dpDisplayPriority = passageID;
            a.width = Config.WINDOW_WIDTH;
            a.height = Config.WINDOW_HEIGHT;
            // refreshUIRealShow(passageData.displayObject, null, passageData, false, false);
            // -- 深度坐标
            a.useDPCoordScaleMode = true;
            a.useDPCoord = true;
            if (passageData.setAttr) {
                a.dpX = passageData.dpX;
                a.dpY = passageData.dpY;
                a.dpZ = passageData.dpZ;
                a.dpScaleX = passageData.dpScaleX;
                a.dpScaleY = passageData.dpScaleY;
                a.rotation1 = passageData.rotation;
                a.opacity = passageData.opacity;
            }
        }
        else {
            uiLayer.addChild(a);
            a.dpDisplayPriority = passageData.dpDisplayPriority;
            if (passageData.setAttr) {
                a.x = passageData.dpX;
                a.y = passageData.dpY;
                a.scaleX = passageData.dpScaleX;
                a.scaleY = passageData.dpScaleY;
                a.width = Config.WINDOW_WIDTH;
                a.height = Config.WINDOW_HEIGHT;
                a.rotation = passageData.rotation;
                a.opacity = passageData.opacity;
            }
        }
        // -- 如果是当前指令的话则选中它
        if (previewWin.currentSelectCmd == passageData.cmd) {
            Callback.CallLaterBeforeRender(() => {
                previewWin.operateObjectTool.selectObjects([a]);
            }, this)
        }
        else if (!previewWin.allowAllDisplayObject) {
            a.mouseEnabled = false;
            a.setTonal(-200, -200, -200, 0);
        }
        previewWin.operateObjectTool.addExcludeOperactionObject(a);
        // ---
        a.on(EventObject.RIGHT_MOUSE_UP, this, (a: UIBitmap) => {
            if (previewWin.viewAreaRightMouseMove) return;
            var menuData: any[] = [
                {
                    label: "定位到对应的指令",
                    splitLine: true,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "selectEvent", // 点击后执行的方法名称
                    shortcutKey: "F", //显示的快捷键名称
                    menuHotKey: "F", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「显示界面」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "createShowUIEvent", // 点击后执行的方法名称
                    shortcutKey: "", //显示的快捷键名称
                    menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「移动界面」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "createMoveUIEvent", // 点击后执行的方法名称
                    shortcutKey: "", //显示的快捷键名称
                    menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「移动界面」+「等待」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "createMoveUIEvent2", // 点击后执行的方法名称
                    shortcutKey: "", //显示的快捷键名称
                    menuHotKey: "", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                },
                {
                    label: "生成「关闭界面」",
                    splitLine: false,// 是否显示分割线
                    children: [], // 存放和本身这个对象是一样的格式
                    execute: "earseEvent", // 点击后执行的方法名称
                    shortcutKey: "D", //显示的快捷键名称
                    menuHotKey: "D", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                    enabled: true, // 是否可用，不填写表示true
                    visible: true, // 是否显示，不填写表示true
                }
            ];
            // 关于生成的指令是插入在上方还是下方
            var isinsertCMDToUp = passageData.cmd == previewWin.currentSelectCmd ? false : true;
            var funcDomain = {
                selectEvent: () => {
                    previewWin.selectCmdMessageByCmd(passageData.cmd, true);
