//------------------------------------------------------------------------------------------------------
// 编辑器预览窗口运行时提供的系统变量
// 关于预览的流程：
// -- 1.imageLayerPassageClear 每已使用的通道x每已使用的指令
// -- 2.runCommand 每个指令按顺序执行一遍
// -- 3.imageLayerPassageStart 每已使用的通道x每已使用的指令
// 关于播放的流程
// -- 1.imageLayerPassageClear 每已使用的通道x每已使用的指令
// -- 2.runCommand 每个指令按顺序播放
//------------------------------------------------------------------------------------------------------
/**
 * 当前自定义指令的编号
 */
declare var commandID: number;
/**
 * 图像层通道数据
 */
declare var imageLayerPassageData: any[];
/**
 * 游戏层（包含图像层和界面层）
 */
declare var gameLayer: GameImageLayer;
/**
 * 图像层显示对象容器
 */
declare var imageLayer: GameImageLayer;
/**
 * 界面层显示对象容器
 */
declare var uiLayer: GameSprite;
/**
 * 预览窗口
 */
declare var previewWin: {
    /**
     * 清理全部事件指令产生的预览效果
     */
    clearData: Function;
    /**
     * 设置数据，刷新预览效果直到当前选中的指令行
     */
    setData: Function;
    /**
     * 当前选中的指令
     */
    currentSelectCmd: Command;
    /**
     * 视图区域是否右键移动过
     */
    viewAreaRightMouseMove: boolean;
    /**
     * 允许显示全部对象
     */
    allowAllDisplayObject: boolean;
    /**
     * 对象操作工具
     */
    operateObjectTool: {
        // 刷新对象操作工具，比如来自其他地方更改了对象的位置或尺寸等，可使用该函数刷新
        refresh: Function,
        // 选中显示对象
        selectObjects: (sp: GameSprite[]) => void,
        // 需要排除缩放和旋转工具的对象
        addExcludeOperactionObject: (sp: GameSprite) => void
    }
    /**
     * 选中事件指令
     * @param cmd 事件指令
     * @param sendEvent 派发事件 默认=false 派发事件会刷新属性栏、预览窗口等
     */
    selectCmdMessageByCmd(cmd: Command, sendEvent?: boolean);
    /**
     * 修改指令属性
     * @param cmd 需要修改的指令
     * @param varName 参数变量名称
     * @param newValue 新的值
     */
    modifyCommandParam: (cmd: Command, varName: string, newValue: any) => void;
    /**
     * 新增指令 默认是默认值，如果有写入参数的话则以参数为准 varNameValue
     * @param customID 自定义ID
     * @param varNameValue 额外的变量值，如存在则优先使用该值，否则使用自定义编辑器中的默认值，如 {dpX:100,dpY:200}
     * @param selectedIndex [可选] 默认值=-2  -3=当前选中（之后） -2=当前选中（之前） -1=最尾端 0-N=自定义 
     * @param refreshPreViewArea [可选] 默认值=true 刷新预览区
     */
    insertCustomCMD(customID: number, varNameValue: any, selectedIndex?: number, refreshPreViewArea?: boolean): Command;
    /**
     * 更改显示层
     * @param selectedIndex 0-界面层 1-图像层
     */
    changeLayer(selectedIndex: number): void;
    /**
     * 创建菜单
     * var menuData:any[] = [
               {
                   label:"重命名",
                   splitLine:false,// 是否显示分割线
                   children:[], // 存放和本身这个对象是一样的格式
                   execute:"functionName", // 点击后执行的方法名称
                   shortcutKey:"", //显示的快捷键名称
                   menuHotKey:"", //实际作用的快捷键名称 对应Laya.KeyBoard的值 如A
                   enabled:true, // 是否可用，不填写表示true
                   visible:true, // 是否显示，不填写表示true
                   selected:true, // 如果存在则以打钩的形式选择此项
                   group:"myGroupName" // 配合selected，如若存在则同组单选
               }
           ];
     * @param menuData 菜单数据
     * @param x 全局坐标X
     * @param y 全局坐标Y
     * @param executeDomain 函数作用域
     */
    createMenu(menuData: any[], x: number, y: number, executeDomain: any);
}
/**
 * 材质相关的数据
 * -- 单一材质编辑器储存的数据：[{ materials: [] },{ materials: [] }] index表示通道
 */
declare class materialPassageData {
    // ====> system 必要项
    /**
     * 通道编号
     */
    passageID: number;
    /**
     * 类别，一般用于区分显示对象，如图片、动画、立绘、界面都是属于不同的类别
     */
    type: string;
    /**
     * 记录关联的指令
     */
    cmd: Command;
    /**
     * 当通道覆盖时调用的函数，以便清理掉。如显示了2号图片或移动了2号图片，再次显示2号图片时会清理掉此前显示的图片或移动行为
     */
    onCoverPassage: Function;

    // ====> custom 自定义属性
    stages: { materials: any[] }[][]; // 图像层 [第一维-指令][第二维-材质通道]
    uiLayers: { materials: any[] }[][]; // 界面层
    screens: { materials: any[] }[][]; // 全画面
    imagePassages: { passageID: number, materialSetting: { materials: any[] }[], recordTargetPassageData: any }[]; // 指定的图像
    uis: { uiID: number, materialSetting: { materials: any[] }[] }[]; // 指定的界面
}
var passageType = "gcMaterial";
//------------------------------------------------------------------------------------------------------
// [everyone used command] 逐指令行目执行
//------------------------------------------------------------------------------------------------------
/**
 * 逐指令执行：对应事件下按照从上至下的顺序逐行执行，如果是该自定义指令时则会执行该函数
 * 该函数用于编辑器预览和播放，如果分歧面板中筛选不经过的事件指令则不会执行该函数。
 * 比如事件页中有3个该指令，则按照顺序执行3次该函数
 * @param cmdParams 指令的自定义参数数据，根据用户设定值
 * @param cmd 对应的指令对象
 * @param isPlayMode 是否是播放模式，否则是普通的预览模式
 */
function runCommand(cmdParams: ThisCommandParams, cmd: Command, isPlayMode: boolean) {
    // -- 图像层的场合 通道编号大于界面的编号以便 imageLayerPassageStart 时在界面之后
    var passageID = 2000001;
    // -- 获取通道数据
    var passageData: materialPassageData = imageLayerPassageData[passageID];
    // -- 获取储存的通道，没有的话则新建
    if (!passageData) {
        passageData = imageLayerPassageData[passageID] = {
            stages: [],
            uiLayers: [],
            screens: [],
            imagePassages: [],
            uis: []
        } as any;
    }
    passageData.passageID = passageID;
    passageData.cmd = cmd;
    passageData.type = passageType;
    // 材质数据设置，格式：[{ materials: [] },{ materials: [] }]  渲染用的materialData不更改，其他数组等容器复制
    var materialData = [];
    for (var i = 0; i < cmdParams.materialData.length; i++) {
        var md = { materials: cmdParams.materialData[i].materials.concat() };
        materialData.push(md);
    }

    // 将需要增加的材质添加至列表中
    // -- 图像层
    if (cmdParams.targetType == 0) {
        passageData.stages.push(materialData);
    }
    // -- 指定的图像编号
    else if (cmdParams.targetType == 1) {
        if (!cmdParams.passageIDUseVar) {
            var imagePassageID = cmdParams.passageID;
            // 设置显示对象通道的覆盖函数
            var displayObjectPasageData = imageLayerPassageData[imagePassageID];
            if (displayObjectPasageData) {
                // 存在该通道的情况才追加
                var materialRenderPassageData = { passageID: imagePassageID, materialSetting: materialData, recordTargetPassageData: displayObjectPasageData }
                passageData.imagePassages.push(materialRenderPassageData);
            }
        }
    }
    // -- 指定的界面
    else if (cmdParams.targetType == 2) {
        if (!cmdParams.objectUseVar) {
            var uiID = cmdParams.uiID;
            var uiPassageID = 1000000 + cmdParams.uiID;
            var displayObjectPasageData = imageLayerPassageData[uiPassageID];
            if (displayObjectPasageData) {
                passageData.uis.push({ uiID: uiID, materialSetting: materialData });
            }
        }
    }
    // -- 界面层
    else if (cmdParams.targetType == 3) {
        passageData.uiLayers.push(materialData);
    }
    // -- 全画面
    else if (cmdParams.targetType == 4) {
        passageData.screens.push(materialData);
    }

    // 播放
    if (isPlayMode) {
        if (cmdParams.targetType == 0) {
            addMaterialToLayer(materialData, imageLayer);
        }
        // -- 指定的图像编号
        else if (cmdParams.targetType == 1) {
            var imagePassageID = cmdParams.passageID;
            var imagePassageInfos = { passageID: imagePassageID, materialSetting: materialData };
            var targetDisplayObjectPassage = imageLayerPassageData[imagePassageInfos.passageID];
            if (!targetDisplayObjectPassage || !targetDisplayObjectPassage.displayObject) return;
            // 遍历所有的通道
            for (var p = 0; p < imagePassageInfos.materialSetting.length; p++) {
                var materialSetting = imagePassageInfos.materialSetting[p];
                // 遍历所有的材质
                for (var s = 0; s < materialSetting.materials.length; s++) {
                    var mData: MaterialData = materialSetting.materials[s];
                    mData.____timeInfo = {};
                    var m = targetDisplayObjectPassage.displayObject.getMaterialByID(mData.id, p);
                    // 如果已存在的话需要移除掉，替换新的同ID材质
                    if (m) targetDisplayObjectPassage.displayObject.removeMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.addMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.setMaterialDirty();
                    // targetDisplayObjectPassage.displayObject.doRepaint();
                }
            }
        }
        // -- 指定的界面
        else if (cmdParams.targetType == 2) {
            var uiID = cmdParams.uiID;
            var uiPassageInfos = { uiID: uiID, materialSetting: materialData };
            var targetPassageID = 1000000 + uiPassageInfos.uiID;
            var targetDisplayObjectPassage = imageLayerPassageData[targetPassageID];
            if (!targetDisplayObjectPassage || !targetDisplayObjectPassage.displayObject) return;
            // 遍历所有的通道
            for (var p = 0; p < uiPassageInfos.materialSetting.length; p++) {
                var materialSetting = uiPassageInfos.materialSetting[p];
                // 遍历所有的材质
                for (var s = 0; s < materialSetting.materials.length; s++) {
                    var mData: MaterialData = materialSetting.materials[s];
                    mData.____timeInfo = {};
                    var m = targetDisplayObjectPassage.displayObject.getMaterialByID(mData.id, p);
                    // 如果已存在的话需要移除掉，替换新的同ID材质
                    if (m) targetDisplayObjectPassage.displayObject.removeMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.addMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.setMaterialDirty();
                    // targetDisplayObjectPassage.displayObject.doRepaint();

                }
            }

        }
        // -- 界面层
        else if (cmdParams.targetType == 3) {
            addMaterialToLayer(materialData, uiLayer);
        }
        // -- 全画面
        else if (cmdParams.targetType == 4) {
            addMaterialToLayer(materialData, gameLayer);
        }
    }
    function addMaterialToLayer(materialData: any[], layer: GameSprite) {
        var stageInfos = materialData;
        // 遍历所有的通道
        for (var p = 0; p < stageInfos.length; p++) {
            var stageInfo = stageInfos[p];
            // 遍历所有的材质
            for (var s = 0; s < stageInfo.materials.length; s++) {
                var mData: MaterialData = stageInfo.materials[s];
                mData.____timeInfo = {};
                var m = layer.getMaterialByID(mData.id, p);
                // 如果已存在的话需要移除掉，替换新的同ID材质
                if (m) layer.removeMaterial(mData, p);
                layer.addMaterial(mData, p);
            }
        }
    }
}
//------------------------------------------------------------------------------------------------------
// 属性面板修改：当修改该指令的属性时，只会执行该指令对应的「指令源」的这些函数
// 如A指令来自自定义指令32号，每次通过属性面板快速修改A指令的参数b时，则会执行32号指令的下面2个函数
// [current used command] 
//------------------------------------------------------------------------------------------------------
/**
 * 当属性面板中的属性修改前
 * @param cmd 对应的指令
 * @param cmdParams 对应指令的参数 
 * @param varName 修改的属性名
 */
function onCommandAttributeBeforeChange(cmd: Command, cmdParams: ThisCommandParams, varName: string, compOnChangeParam: any) {
    // 修改了通道的话，清理
    if (varName != "materialData" || compOnChangeParam == 1) {
        previewWin.clearData();
    }
}
/**
 * 当属性面板中的属性修改后
 * @param cmd 对应的指令
 * @param cmdParams 对应指令的参数 
 * @param varName 修改的属性名
 * @param newValue 修改后的值
 */
function onCommandAttributeChange(cmd: Command, cmdParams: ThisCommandParams, varName: string, newValue: any, compOnChangeParam: any) {
    // 修改了通道的话 compOnChangeParam==1 表示如删除材质/添加材质等需要刷新的操作
    if (varName != "materialData" || compOnChangeParam == 1) {
        previewWin.setData(false);
        return;
    }
    // 修改材质参数后刷新（仅修改已有材质的参数）
    // -- 画面
    var targetSp: GameSprite;
    if (cmdParams.targetType == 0) {
        targetSp = imageLayer;
    }
    // -- 指定的图像编号
    else if (cmdParams.targetType == 1) {
        if (!cmdParams.passageIDUseVar) {
            var imagePassageID = cmdParams.passageID;
            var passageData: any = imageLayerPassageData[imagePassageID];
            if (passageData) targetSp = passageData.displayObject;
        }
    }
    // -- 指定的界面
    else if (cmdParams.targetType == 2) {
        if (!cmdParams.objectUseVar) {
            var uiID = cmdParams.uiID;
            var passageData: any = imageLayerPassageData[uiID + 1000000];
            if (passageData) targetSp = passageData.displayObject;
        }
    }
    // -- 界面层
    else if (cmdParams.targetType == 3) {
        targetSp = uiLayer;
    }
    // -- 全画面
    else if (cmdParams.targetType == 4) {
        targetSp = gameLayer;
    }
    if (targetSp) {
        targetSp.setMaterialDirty();
    }
}
//------------------------------------------------------------------------------------------------------
// 对象操作工具
// [everyone used command type] 遍历每个执行过的自定义指令类别
