//------------------------------------------------------------------------------------------------------
// 图像层相关 遍历每个已使用的通道，每个已使用的通道再遍历已使用的指令，调用下面的函数
// [everyone passage] x [everyone used command type]
//------------------------------------------------------------------------------------------------------
/**
 * 清理函数：每次刷新前调用此函数进行清理
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageClear(passageID: number, passageData: materialPassageData) {
    if (passageData.type == passageType) {
        // -- 清理图像层的材质
        imageLayer.clearMaterials();
        // -- 清理指定图像通道的材质
        for (var i = 0; i < passageData.imagePassages.length; i++) {
            var imagePassageInfo = passageData.imagePassages[i];
            var targetPassageID = imagePassageInfo.passageID;
            var targetPassageData: any = imageLayerPassageData[targetPassageID];
            if (targetPassageData && targetPassageData.displayObject) {
                (targetPassageData.displayObject as GameSprite).clearMaterials();
            }
        }
        // -- 清理指定编号的界面的
        for (var i = 0; i < passageData.uis.length; i++) {
            var uiPassageInfo = passageData.uis[i];
            var targetUIID = uiPassageInfo.uiID;
            var targetPassageID = 1000000 + targetUIID;
            var targetPassageData: any = imageLayerPassageData[targetPassageID];
            if (targetPassageData && targetPassageData.displayObject) {
                (targetPassageData.displayObject as GameSprite).clearMaterials();
            }
        }
        // -- 清理界面层的材质
        uiLayer.clearMaterials();
        // -- 清理全画面
        gameLayer.clearMaterials();
        // 清理记录
        passageData.stages = [];
        passageData.uiLayers = [];
        passageData.screens = [];
        passageData.imagePassages = [];
        passageData.uis = [];
    }
}
/**
 * 开始函数
 * @param passageID 通道
 * @param passageData 通道储存的数据
 */
function imageLayerPassageStart(passageID: number, passageData: materialPassageData) {
    if (passageData.type == passageType) {
        passageData.onCoverPassage = imageLayerPassageClear;// v0.985 upgrader
        // -- 图像层
        addMaterialToLayer(passageData.stages, imageLayer);
        // -- 指定的图像
        for (var i = 0; i < passageData.imagePassages.length; i++) {
            var imagePassageInfos = passageData.imagePassages[i];
            var targetDisplayObjectPassage = imageLayerPassageData[imagePassageInfos.passageID];
            // 不存在目标通道的话
            if (!targetDisplayObjectPassage || !targetDisplayObjectPassage.displayObject) continue;
            // 目标通道不是我记录的通道的话（比如先使用材质，然后通道又重置了）
            if (targetDisplayObjectPassage != imagePassageInfos.recordTargetPassageData) continue;
            // 遍历所有的通道
            for (var p = 0; p < imagePassageInfos.materialSetting.length; p++) {
                var materialSetting = imagePassageInfos.materialSetting[p];
                // 遍历所有的材质
                for (var s = 0; s < materialSetting.materials.length; s++) {
                    var mData: MaterialData = materialSetting.materials[s];
                    mData.____timeInfo = {};
                    var m = targetDisplayObjectPassage.displayObject.getMaterialByID(mData.id, p);
                    // 如果已存在的话需要移除掉，替换新的同ID材质
                    if (m) targetDisplayObjectPassage.displayObject.removeMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.addMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.setMaterialDirty();
                    // targetDisplayObjectPassage.displayObject.doRepaint();
                }
            }
        }
        // -- 指定的界面
        for (var i = 0; i < passageData.uis.length; i++) {
            var uiPassageInfos = passageData.uis[i];
            var targetPassageID = 1000000 + uiPassageInfos.uiID;
            var targetDisplayObjectPassage = imageLayerPassageData[targetPassageID];
            if (!targetDisplayObjectPassage || !targetDisplayObjectPassage.displayObject) continue;
            // 遍历所有的通道
            for (var p = 0; p < uiPassageInfos.materialSetting.length; p++) {
                var materialSetting = uiPassageInfos.materialSetting[p];
                // 遍历所有的材质
                for (var s = 0; s < materialSetting.materials.length; s++) {
                    var mData: MaterialData = materialSetting.materials[s];
                    mData.____timeInfo = {};
                    var m = targetDisplayObjectPassage.displayObject.getMaterialByID(mData.id, p);
                    // 如果已存在的话需要移除掉，替换新的同ID材质
                    if (m) targetDisplayObjectPassage.displayObject.removeMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.addMaterial(mData, p);
                    targetDisplayObjectPassage.displayObject.setMaterialDirty();
                    // targetDisplayObjectPassage.displayObject.doRepaint();

                }
            }
        }
        // -- 界面层
        addMaterialToLayer(passageData.uiLayers, uiLayer);
        // -- 全画面
        addMaterialToLayer(passageData.screens, gameLayer);
    }
    function addMaterialToLayer(passageDatas: { materials: any[] }[][], layer: GameSprite) {
        for (var i = 0; i < passageDatas.length; i++) {
            var stageInfos = passageDatas[i];
            // 遍历所有的通道
            for (var p = 0; p < stageInfos.length; p++) {
                var stageInfo = stageInfos[p];
                // 遍历所有的材质
                for (var s = 0; s < stageInfo.materials.length; s++) {
                    var mData: MaterialData = stageInfo.materials[s];
                    mData.____timeInfo = {};
                    var m = layer.getMaterialByID(mData.id, p);
                    // 如果已存在的话需要移除掉，替换新的同ID材质
                    if (m) layer.removeMaterial(mData, p);
                    layer.addMaterial(mData, p);
                }
            }
        }
        layer.setMaterialDirty();
    }
}
