customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// 获取自定义游戏数值描述的函数：getCustomGameNumber(customGameNumber: any[]):string;
// 获取自定义游戏字符串描述的函数：getCustomGameString(customGameString: any[]):string;
// 获取自定义游戏开关描述的函数：getCustomGameSwitch(customGameSwitch: any[]):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象原型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-事件库列表 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义指令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表 25-材质列表 26-场景对象模块
let value: any;
if (attrs.worldData.value.varType <= 2) {
    let mapping = { 0: 3, 1: 5, 2: 4 }
    value = attrs.worldData.valueType != 0 ? `[@${attrs.worldData.value.value}-${getTypeDataListName(mapping[attrs.worldData.value.varType], attrs.worldData.value.value)}]` : attrs.worldData.value.value;
}
else {
    value = attrs.worldData.value.value;
}
let mapping = { 0: 3, 1: 5, 2: 4 };
let sy = attrs.worldData.operationType == null || attrs.worldData.value.varType != 0 ? " = " : [" = ", " += ", " -= ", " *= ", " /= ", " %= ", " ^= "][attrs.worldData.operationType];
if (attrs.worldData.selectMode == 1) {
    let mode = attrs.worldData.inputModeInfo.mode;
    let constName = attrs.worldData.inputModeInfo.constName;
    let varNameIndex = attrs.worldData.inputModeInfo.varNameIndex;
    let varName = mode == 0 ? constName : `[@${varNameIndex}-${getTypeDataListName(mapping[varNameIndex], varNameIndex)}]`;
    ` ${varName}${sy}${value}`
}
else {
    ` ${attrs.worldData.varName}${sy}${value}`
}

customPhraseDescJS:
var value;
if (attrs.worldData.value.varType <= 2) {
    var mapping_1 = { 0: 3, 1: 5, 2: 4 };
    value = attrs.worldData.valueType != 0 ? "[@" + attrs.worldData.value.value + "-" + getTypeDataListName(mapping_1[attrs.worldData.value.varType], attrs.worldData.value.value) + "]" : attrs.worldData.value.value;
}
else {
    value = attrs.worldData.value.value;
}
var mapping = { 0: 3, 1: 5, 2: 4 };
var sy = attrs.worldData.operationType == null || attrs.worldData.value.varType != 0 ? " = " : [" = ", " += ", " -= ", " *= ", " /= ", " %= ", " ^= "][attrs.worldData.operationType];
if (attrs.worldData.selectMode == 1) {
    var mode = attrs.worldData.inputModeInfo.mode;
    var constName = attrs.worldData.inputModeInfo.constName;
    var varNameIndex = attrs.worldData.inputModeInfo.varNameIndex;
    var varName = mode == 0 ? constName : "[@" + varNameIndex + "-" + getTypeDataListName(mapping[varNameIndex], varNameIndex) + "]";
    " " + varName + sy + value;
}
else {
    " " + attrs.worldData.varName + sy + value;
}
