customPhraseDescTS:
// 根据属性名称值编写自定义短语描述，如："我的短语是"+attrs.xxx
// 使用下拉框组件的变量attrs.xxx有对应的attrs.xxxItems的属性以便获得标签名集合
// 获取自定义模块的数据名称的函数：getModuleDataName(moduleID:number,dataID:number):string;
// 获取各种列表内数据名称的函数：getTypeDataListName(typeID: number, dataID: number):string;
// -- typeID ：0-全局变量 1-全局开关 2-全局字符串 3-玩家变量 4-玩家开关 5-玩家字符串
//           ：6-场景列表 7-场景对象模型列表 8-图块列表 9-行走图列表 10-行走图动作列表 11-立绘列表 12-立绘表情列表
//           ：13-公共事件 14-对话框列表 15-动画列表 16-动画信号列表 17-界面列表 18-数据结构列表 19-自定义模块列表
//           ：20-自定义命令列表 21-自定义行为列表 22-自动元件列表 23-行走图部件列表 24-立绘部件列表
let moduleID = attrs.modelData.moduleID;
let moduleName = getTypeDataListName(19, moduleID);
let dataName = attrs.modelData.dataIsUseVar ? `[@v${attrs.modelData.dataVarID}-${getTypeDataListName(3, attrs.modelData.dataVarID)}]` : `[${attrs.modelData.dataID}-${getModuleDataName(moduleID, attrs.modelData.dataID)}]`;
let mapping = { 0: 3, 1: 5, 2: 4 };
if (attrs.modelData.selectMode == 1) {
    let mode = attrs.modelData.inputModeInfo.mode;
    let constName = attrs.modelData.inputModeInfo.constName;
    let varNameIndex = attrs.modelData.inputModeInfo.varNameIndex;
    let varName = mode == 0 ? constName : `[@${varNameIndex}-${getTypeDataListName(mapping[varNameIndex], varNameIndex)}]`;
    `${moduleID}-${moduleName}-${dataName}:${varName}`
}
else {
    `${moduleID}-${moduleName}-${dataName}:${attrs.modelData.varName}`
}

customPhraseDescJS:
var moduleID = attrs.modelData.moduleID;
var moduleName = getTypeDataListName(19, moduleID);
var dataName = attrs.modelData.dataIsUseVar ? "[@v" + attrs.modelData.dataVarID + "-" + getTypeDataListName(3, attrs.modelData.dataVarID) + "]" : "[" + attrs.modelData.dataID + "-" + getModuleDataName(moduleID, attrs.modelData.dataID) + "]";
var mapping = { 0: 3, 1: 5, 2: 4 };
if (attrs.modelData.selectMode == 1) {
    var mode = attrs.modelData.inputModeInfo.mode;
    var constName = attrs.modelData.inputModeInfo.constName;
    var varNameIndex = attrs.modelData.inputModeInfo.varNameIndex;
    var varName = mode == 0 ? constName : "[@" + varNameIndex + "-" + getTypeDataListName(mapping[varNameIndex], varNameIndex) + "]";
    moduleID + "-" + moduleName + "-" + dataName + ":" + varName;
}
else {
    moduleID + "-" + moduleName + "-" + dataName + ":" + attrs.modelData.varName;
}
