// varying vec2 v_texcoord; //  原始贴图UV坐标
// uniform sampler2D texture; // 原始贴图
// uniform float alpha; // 当前对象实际渲染的透明度
//------------------------------------------------------------------------------------------------------
// 该材质的自定义参数 判定该变量存在的宏：MU材质编号_变量名称
//------------------------------------------------------------------------------------------------------
// uniform float mu3_strength; // MU3_strength 强度
// uniform float mu3_time; // MU3_time 时间设定
// uniform vec2 renderTargetSize; // 渲染对象尺寸信息 x=宽度 y=高度
//------------------------------------------------------------------------------------------------------
// shader 代码规则：
// 1.由于多材质合并shader代码运行，函数名必须以mf${材质ID}_开头以避免函数名称冲突，如 mf3_myFunc
// 2.入口函数vec4 mf${材质ID}_main 如 vec4 mf3_main(){return gl_FragColor;}
// 3.原图采样坐标获取：vec2 texCoord = getInRangeTextureCoord(v_texcoord);
// 4.原图采样：vec4 color = getTextureColor(texCoord);
// 5.关于自定义贴图的坐标信息：自定义贴图可能是图集中的一部分，提供了相关信息（mu材质编号_变量名_UVInfo）
//   相对于0~1的坐标系提供了图集的映射信息 vec4 [UV宽度缩放比例, UV高度缩放比例, UV水平偏移量, UV垂直偏移量]
// 6.变量宏（可用于判定该变量是否满足出现条件）：#ifdef MU材质编号_变量名称、#else、#endif
// 7.系统宏 IN_GC_EDITOR=是否在编辑器中运行
// 8.语法：C语言 更多资料请参考GLSL Shader
//======================================================================================================
// SYSTEM END
//======================================================================================================
vec4 mf3_main(){
   #ifdef MU3_time
      float strength = mu3_strength * mu3_time;
   #else
      float strength = mu3_strength;
   #endif
   // 如果强度为0则直接返回
   if(strength==0.0)return gl_FragColor;
   // 周围NxN像素，以自己为中心 blurw越大则效果越好，但越消耗性能
   const float blurw = 19.0;
   float blurw10 = (blurw+1.0);
   float blurw5 = blurw10/2.0;
   float blurw100 = blurw10*blurw10;
   float blurw50 =  blurw100/2.0;
   float blurwP1 = 1.0/blurw100;
   // 获取单位像素，并根据强度进行扩散
   float dx = 1.0/renderTargetSize.x * strength/3.0; // 100=(blurw+1)^2
   float dy = 1.0/renderTargetSize.y * strength/3.0; // 100=(blurw+1)^2
   // 
   vec4 vec4Color = vec4(0.0,0.0,0.0,0.0);
   float halfDx = dx * blurw50;
   float halfDy = dy * blurw50;
   // 起始坐标：当前坐标点 - N/2
   vec2 startpos=vec2(v_texcoord.x-halfDx,v_texcoord.y-halfDy);
   vec2 ctexcoord = startpos;
   // 分摊的颜色
   float pColorPer = blurwP1;
   // 采样固定偏移
   float fixDx = halfDx - dx*blurw5;
   float fixDy = halfDy - dy*blurw5;
   // 采样以自己为中心采样 N x N 像素进行混合
   for(float y = 0.0;y<=blurw; ++y) {
	 for(float x = 0.0;x<=blurw; ++x) {
        ctexcoord.x = startpos.x + dx*x + fixDx;
	    ctexcoord.y = startpos.y + dy*y + fixDy;
		float xPer = (1.0-abs((x-blurw5)/blurw5));
		float yPer = (1.0-abs((y-blurw5)/blurw5));
		vec4Color += getTextureColor(ctexcoord)*pColorPer*xPer*yPer*4.0;
	 }
   }
   vec4Color.a*=alpha;
   return vec4Color;
}
