/**
 * 图像层通道数据
 */
declare var imageLayerPassageData: any[];
/**
 * 小类别 0-无 1-图片 2-音频 3-界面 4-视频 5-动画 6-自定义格式文件 7-立绘 
 */
declare var classID: number;
/**
 * 值 
 * 图片/音频/自定义格式文件 = 路径:string;
 * 界面/动画/立绘 = 编号:number;
 */
declare var value: any;
/**
 * 拖拽至图像层的坐标
 */
declare var inImageLayerX: number;
declare var inImageLayerY: number;
/**
 * 预览窗口
 */
declare var previewWin: {
    /**
     * 新增指令 默认是默认值，如果有写入参数的话则以参数为准 varNameValue
     * @param customID 自定义ID
     * @param varNameValue 额外的变量值，如存在则优先使用该值，否则使用自定义编辑器中的默认值，如 {dpX:100,dpY:200}
     * @param selectedIndex [可选] 默认值=-2  -3=当前选中（之后） -2=当前选中（之前） -1=最尾端 0-N=自定义 
     * @param refreshPreViewArea [可选] 默认值=true 刷新预览区
     */
    insertCustomCMD(customID: number, varNameValue: any, selectedIndex?: number, refreshPreViewArea?: boolean): Command;
    /**
     * 更改显示层
     * @param selectedIndex 0-界面层 1-图像层
     */
    changeLayer(selectedIndex: number): void;
}
//------------------------------------------------------------------------------------------------------
// 编写资源对应的插入指令逻辑
//------------------------------------------------------------------------------------------------------
// 对应的指令
var PLUGIN_CUSTOM_COMMAND_showPicture: number = 3001;
// 加载贴图，存在的话则以该贴图原始尺寸插入对应的指令
AssetManager.loadImage(value, Callback.New((tex: Texture) => {
    if (tex) {
        // 获取空的通道
        var passageID = 1;
        for (var i = 1; i < 9999; i++) {
            if (!imageLayerPassageData[i]) {
                passageID = i;
                break;
            }
        }
        // 插入代码
        previewWin.insertCustomCMD(PLUGIN_CUSTOM_COMMAND_showPicture, {
            image: value,
            passageID: passageID,
            dpWidth: tex.width,
            dpHeight: tex.height,
            dpX: Math.floor(inImageLayerX - tex.width / 2),
            dpY: Math.floor(inImageLayerY - tex.height / 2)
        }, -3, true);
        // 切换为图像层
        previewWin.changeLayer(1);
    }
}, this));

